mapper.insert:

	SELECT seq_classnotice.nextval FROM dual

INSERT INTO classnotice (
cn_no
, cn_category
, cn_title
, cn_content
, cn_reg_date
, cn_top_yn
, cn_attaches
, cn_mem_id )
VALUES (
#{cnNo}
, #{cnCategory}
, #{cnTitle}
, #{cnContent}
, SYSDATE
, #{cnTopYn}
, #{cnAttaches}
, #{cnMemId} )

mapper.update:
UPDATE classnotice
SET cn_title = #{cnTitle}
, cn_category = #{cnCategory}
, cn_content = #{cnContent}
, cn_mod_date = SYSDATE
, cn_top_yn = #{cnTopYn}
WHERE cn_no = #{cnNo}
