mapper.select:
SELECT cq_no , cq_category , cq_title , TO_CHAR(NVL(cq_mod_date,cq_reg_date),'YYYY-MM-DD') AS cq_reg_date , cq_mem_id , (SELECT mem_name FROM member WHERE mem_id = cq_mem_id) AS cq_mem_name , cq_answer_yn FROM classquestion where 1 = 1
and cq_mem_id = #{cqMemId}
ORDER BY cq_no DESC

mapper.update:
	UPDATE classquestion
	   SET cq_title    = #{cqTitle}
	     , cq_category = #{cqCategory}
	     , cq_content  = #{cqContent}
	     , cq_mod_date = SYSDATE
     WHERE cq_no = #{cqNo}
       AND cq_answer_yn = 'N'

mapper.delete:
DELETE FROM classquestion 
 WHERE cq_no = #{cqNo}
   AND cq_answer_yn = 'N'

mapper.update:
	UPDATE classquestion
	   SET ca_content  = #{caContent}
	     , ca_reg_date = SYSDATE
	     , ca_mem_id   = #{caMemId}
	     , cq_answer_yn = 'Y'
     WHERE cq_no = #{cqNo}
