mapper.select:
SELECT count(*)
  FROM SITEQUESTION
 WHERE sq_writer = #{userName}

mapper.insert:

 			SELECT SEQ_SITEQUESTION.NEXTVAL FROM DUAL

 		INSERT INTO SITEQUESTION (
				    sq_no
				    , sq_title
				    , sq_content
				    , sq_reg_date
				    , sq_answer_yn
				    , sq_attaches
				    , sq_category
				    , sq_mem_id
				    , sq_writer
		   ) VALUES (

				      #{sqNo}
				    , #{sqTitle}
				    , #{sqContent}
				    , SYSDATE
				    , '답변중'
				    , #{sqAttaches}
				    , #{sqCategory}
				    , #{sqMemId}
				    , #{sqWriter}
				    )

mapper.update:
 		UPDATE SITEQUESTION

		   SET sq_title = #{sqTitle}
			 , sq_content = #{sqContent}
		     , sq_mod_date = SYSDATE
			 , sq_attaches = #{sqAttaches}
			 , sq_category = #{sqCategory}
			 , sq_writer = #{sqWriter} 
		 WHERE sq_no = #{sqNo}

 		UPDATE SITEQUESTION

		   SET sq_answer_yn = '답변완료'
		 WHERE sq_no = #{sqNo}
